mapper.select:
select * from task_todo
where 1=1


        and user_id=#{userId}




        and complete is false




        and note_id=#{noteId}




        and project_id=#{projectId}


order by complete, create_time desc
limit #{size} offset #{offset}

select count(*) as total from task_todo
where 1=1


        and user_id=#{userId}




        and note_id=#{noteId}




        and complete is false




        and project_id=#{projectId}

mapper.update:
update task_todo


        task_title=#{title},


        priority=#{priority},

    project_id=#{projectId},

        complete=#{complete}


where task_id=#{taskId}

mapper.delete:
delete
from task_todo
where 1=1


        and task_id = #{taskId}




        and note_id=#{noteId}
