mapper.select:
select *
from topic t
left join content_detail cd on index_id = topic_id
where 1=1


        and pid=#{pid}


        and pid is null




        and status=#{status}


order by create_time desc
limit #{size}
offset #{offset}

mapper.update:
update topic


        status=#{status},


        views=#{views},


        comments=#{comments}


where topic_id=#{topicId}
